import React from 'react';
import axios from 'axios';
import User from './User';
import UserTracks from './UserTracks';
import { authEndpoint, clientId, redirectUri, scopes } from "./config";
import hash from './hash';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      token: null,
      request: "",
      items: [{
        artists: [{ name: ""}],
        name: "",
        album: {
          images: [{url: ""}]
        },
        images: [{url: ""}],
        external_urls: {spotify: ""},
      }],
      no_data: true,
    };

    this.getUserTopArtist = this.getUserTopArtist.bind(this)

  }

  handleClick(event) {
    const initialState = this.state;
    this.state = initialState;
    const { token } = this.state;
    this.setState({request: event})
    this.getUserTopArtist(token, event)
    console.log("handleArtsit Click RAN")
  }


  componentDidMount() {
    // Sets token
    let _token = hash.access_token;

    if (_token) {
      this.setState({
        token: _token
      });
      console.log("componentDidMount RAN")
    }
  }

  getUserTopArtist(token, request) {
    console.log("axios.get RAN")
    axios.get(`https://api.spotify.com/v1/me/top/${request}`, {
      params: {limit: 10},
      headers: {
        Authorization: "Bearer " + token,
      }
    })
    .then(res => {
      const userData = res.data;
      console.log("getUserTopArtist .THEN")
      this.setState({
        items: userData.items,
        no_data: false 
      });
      console.log(userData.items)
    });
  }  

  render() {
    return (
      <div className="App">
        <header className="App-header">
          {!this.state.token && (
          <div>
            <h1>See your top ranked artist!</h1>
            <a
              className="App-link"
              href={`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join("%20")}&response_type=token&show_dialog=true`}
            >
              Login to Spotify
            </a>
          </div>
          )}
          {this.state.token && (
            <div>
              <button onClick={() => this.handleClick("artists")}> click for artists</button>
              <button onClick={() => this.handleClick("tracks")}> click for Tracks</button>
            </div>
          )}
          {this.state.token && !this.state.no_data && this.state.request === "artists" &&(
            <User items={this.state.items} className="user"/>
          )}
           {this.state.token && !this.state.no_data && this.state.request === "tracks" && (
            <UserTracks items={this.state.items} className="user"/>
          )}
        </header>
      </div>
    );
  }  
}

export default App;
