import React from 'react';
import axios from 'axios';
import User from './User';
import UserTracks from './UserTracks';
import { authEndpoint, clientId, redirectUri, scopes } from "./config";
import hash from './hash';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      token: null,
      request: "",
      logged: true,
      items: [{
        artists: [{ name: ""}],
        name: "",
        album: {
          images: [{url: ""}]
        },
        images: [{url: ""}],
        external_urls: {spotify: ""},
      }],
      no_data: false,
    };

    this.getUserTopArtist = this.getUserTopArtist.bind(this)
    this.tick = this.tick.bind(this);
  }

  handleArtistClick() {
    this.setState({logged: true, request: "artists"})
    this.componentDidMount();
  }

  handleTrackClick() {
    this.setState({logged: false, request: "tracks"})
    this.componentDidMount();
  }

  componentDidMount() {
    // Sets token
    let _token = hash.access_token;

    if (_token) {
      this.setState({
        token: _token
      });
      this.getUserTopArtist(_token);
    }
    this.interval = setInterval(() => this.tick(), 5000);
  }

  componentWillMount() {
    clearInterval(this.interval);
  }

  tick() {
    if(this.state.token) {
      this.getUserTopArtist(this.state.token);
    }
  }


  getUserTopArtist(token) {
    axios.get(`https://api.spotify.com/v1/me/top/${this.state.request}`, {
      params: {limit: 10},
      headers: {
        Authorization: "Bearer " + token,
      }
    })
    .then(res => {
      const userData = res.data;
      if(!userData) {
        this.setState({
          no_data: true,
        });
        return;
      }
      this.setState({
        items: userData.items,
        no_data: false 
      });
      console.log(userData.items)
    });
  }  

  render() {
    return (
      <div className="App">
        <header className="App-header">
          {!this.state.token && (
          <div>
            <h1>See your top ranked artist!</h1>
            <a
              className="App-link"
              href={`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join("%20")}&response_type=token&show_dialog=true`}
            >
              Login to Spotify
            </a>
          </div>
          )}
          {this.state.token && (
            <div>
              <button onClick={() => this.handleArtistClick()}> click for artists</button>
              <button onClick={() => this.handleTrackClick()}> click for tracks</button>
            </div>
          )}
          {this.state.token && !this.state.no_data && this.state.logged && (
            <User items={this.state.items} show={this.state.request} className="user"/>
          )}
           {this.state.token && !this.state.no_data && !this.state.logged && (
            <UserTracks items={this.state.items} show={this.state.request} className="user"/>
          )}
        </header>
      </div>
    );
  }  
}

export default App;
